$(document).ready(function(){
    setInterval(function() { 
        var tam = $(window).width();
        if (tam >= 992){
            $("#banner").show();
            $("#bannerMobile").hide();
        }else{
            $("#bannerMobile").show();
            $("#banner").hide();
        } 
        if(tam <= 690){
            $(".text").addClass("diminuiFont");
            $(".text").removeClass("aumentaFont");
            $("#ponta").hide();
            $("#app1").addClass("empurra");
            $("#app2").addClass("empurra");
        }
        if(tam > 690){
            $(".text").addClass("aumentaFont");
            $(".text").removeClass("diminuiFont");
            $("#app1").removeClass("empurra");
            $("#app2").removeClass("empurra");
            $("#ponta").show();
        }
    }, 2000);
});